"use client";

import { useState } from "react";
import Image, { ImageProps } from "next/image";
import { getSafeImageUrl } from "@/utils/imageUtils";

// Default base64 placeholder image
const DEFAULT_PLACEHOLDER =
  "data:image/jpeg;base64,";

interface SafeImageProps extends Omit<ImageProps, "src"> {
  src: string;
  fallbackSrc?: string;
}

/**
 * A wrapper component for Next.js Image that handles various image sources safely
 * including external URLs, base64, and data URLs, with error handling
 */
export default function SafeImage({
  src,
  fallbackSrc = DEFAULT_PLACEHOLDER,
  alt,
  ...props
}: SafeImageProps) {
  const [error, setError] = useState(false);

  // Process the src to ensure it's safe for Next.js Image
  const processedSrc = error ? fallbackSrc : getSafeImageUrl(src);

  return (
    <Image
      src={processedSrc}
      alt={alt}
      onError={() => setError(true)}
      {...props}
    />
  );
}
